CREATE TABLE `magic`.`expansion` (
  `code` VARCHAR(4) NOT NULL,
  `name` VARCHAR(60) NOT NULL,
  `card_quantity` INT,
  `type` VARCHAR(20),
  `release_date` DATE NOT NULL,
  `block` VARCHAR(45),
  PRIMARY KEY (`code`, `name`),
  UNIQUE INDEX `name_code_UNIQUE` (`code` ASC, `name` ASC) INVISIBLE,
  INDEX `name_IDX` (`name` ASC) VISIBLE);

